import React, { Component } from 'react';
import styled from 'styled-components';
import cloneDeep from 'lodash.clonedeep';

import Timer from "../timer";
import Options from './../options';
import Modal from "../modal";
import Btn from "../btn";
import Header from "../header";
import colors from "../../theme/colors";
import Counter from "../counter";

const PomodoroTimerContainer = styled.div`
    position: relative;

    padding: 50px;
    background: ${({ timerState, pomodoroState }) => {
      const isBreak = pomodoroState === 'shortBreak' || pomodoroState === 'longBreak';
      
      if (timerState === 'started' && pomodoroState === 'work') {
        return colors.timerBg.work;
      } else if (timerState === 'started' && isBreak) {
        return colors.timerBg.break;
      } else { // If stopped, paused, or just wrong condition
        return colors.appBg;
      }
    }};
    
    border-radius: 3px;
    box-shadow: 0 2px 3px 0 ${colors.shadow};
`;

const PomodoroCounter = styled(Counter)`
    position: absolute;
    top: 145px;
    left: 645px;
    color: rgba(0, 0, 0, 0.6);
`;

const PomodoroOptionsBtn = styled(Btn)`
    position: absolute;
    top: 50px;
    right: 50px;
`;

class PomodoroTimer extends Component {
  state = {
    currentStage: null, // 'work' || 'shortBreak' || 'longBreak'
    timerState: null,
    completedTimers: 0,
    completedWorks: 0,
    options: {
      duration: { // in ms
        shortBreak: 5 * 60 * 1000,
        longBreak: 20 * 60 * 1000,
        work: 40 * 60 * 1000,
      },
    },
  };

  finishSound = new Audio('data:audio/mpeg;base64,');

  componentDidMount() {
    this.setStoredState();
    this.updateStage();
  }

  componentDidUpdate(prevProps, prevState) {
    const { completedWorks, completedTimers } = this.state;
    localStorage.setItem('PomodoroTimer', JSON.stringify(this.state)); // Save state in localStorage

    if (completedWorks >= 12) { // Finish timer if all work stages completed
      this.finishPomodoroTimer();
    }

    const timerCompleted = completedTimers !== prevState.completedTimers;
    if (timerCompleted) { // If was incremented
      this.updateStage();
    }
  }

  /**
   * Get saved in localStorage options
   */
  setStoredState() {
    const storedStateStr = localStorage.getItem('PomodoroTimer');
    if (storedStateStr) { // If there is saved data in localStorage, try to parse and set in the state
      try {
        const storedState = JSON.parse(storedStateStr);
        this.setState((prevState) => Object.assign(prevState, storedState)); // In case of new properties
      } catch(e) {
        console.error(e);
      }
    }
  }

  /**
   * Update current stage
   */
  updateStage() {
    const { completedWorks, completedTimers } = this.state;

    let newStage;
    if (completedTimers % 2 === 0) { // Work timers start from 0 (2, 4, 6...)
      newStage = 'work';
    } else {
      newStage = (completedWorks % 4 === 0) ? 'longBreak' : 'shortBreak';
    }

    this.setState({
      currentStage: newStage,
    });
  }

  /**
   * Options for timer
   * @param options
   */
  refreshOptions = (options) => {
    const { duration: durationInMinutes } = options;

    let durationInMs = {};
    for (let stage in durationInMinutes) {
      durationInMs[stage] = durationInMinutes[stage] * 60 * 1000; // Convert to ms
    }

    this.setState({
      options: {
        duration: durationInMs,
      },
    });
  };

  /**
   * Convert options from ms to minutes
   */
  getOptionsInMinutes() {
    const { options } = this.state;
    const { duration: durationInMs } = options;
    const optionsInMinutes = cloneDeep(options);

    for (let stage in durationInMs) {
      const valueInMs = durationInMs[stage];
      const valueInMinutes = Math.floor(valueInMs / 1000 / 60);
      optionsInMinutes.duration[stage] = valueInMinutes;
    }

    return optionsInMinutes;
  }

  playFinishSound() {
    this.finishSound.play();
  }

  /**
   * Increment completedWorks if current state is 'work'
   */
  updateCompletedWorks() {
    const { currentStage } = this.state;
    if (currentStage === 'work') {
      this.setState((prevState) => {
        return {
          completedWorks: prevState.completedWorks + 1,
        };
      })
    }
  }

  incrementCompletedTimers() {
    this.setState((prevState) => {
      return {
        completedTimers: prevState.completedTimers + 1
      };
    });
  }

  handleTimerStop() {
    this.playFinishSound();
    this.updateCompletedWorks();
    this.incrementCompletedTimers();
  }

  finishPomodoroTimer() {
    alert('You have completed all 12 pomodoros');

    this.setState({
      completedWorks: 0,
      completedTimers: 0,
    });
  }

  /**
   * Save state of the timer in the Pomodoro (e.g. for styling)
   * @param {string} timerState
   */
  handleTimerStateChange = (timerState) => {
    if (timerState === 'stopped') {
      this.handleTimerStop();
    }

    this.setState({
      timerState,
    });
  };

  /**
   * Get duration for current stage
   * @returns {number} - Duration in ms
   */
  getCurrentDuration() {
    const { currentStage, options } = this.state;
    return options.duration[currentStage] || 0; // If stage isn't init yet
  }

  render() {
    const { timerState, currentStage, completedWorks } = this.state;
    const currentDuration = this.getCurrentDuration();
    const initialValues = this.getOptionsInMinutes();

    return (
      <PomodoroTimerContainer timerState={timerState} pomodoroState={currentStage}>
        <Header/>
        <Timer duration={currentDuration}
               handleStateChange={this.handleTimerStateChange} />
        <PomodoroCounter completed={completedWorks} all={12} />
        <Modal title="Options"
               trigger={(
                 <PomodoroOptionsBtn type="button">
                   <i className="fas fa-cog"/>
                   <span className="visuallyhidden">Options</span>
                 </PomodoroOptionsBtn>
               )}>
          <Options refreshOptions={this.refreshOptions}
                   initialValues={initialValues}  />
        </Modal>
      </PomodoroTimerContainer>
    );
  }
}

export default PomodoroTimer;
